export const DEFAULT_ID_PREFIX = "root";
export const DEFAULT_ID_SEPARATOR = ".";
export const DEFAULT_ID_PSEUDO_SEPARATOR = "::";
export function pathToId(path, { idPrefix = DEFAULT_ID_PREFIX, idSeparator = DEFAULT_ID_SEPARATOR, } = {}) {
    return (path.length === 0
        ? idPrefix
        : `${idPrefix}${idSeparator}${path.join(idSeparator)}`);
}
export function createPseudoId(instanceId, element, { idPseudoSeparator = DEFAULT_ID_PSEUDO_SEPARATOR, } = {}) {
    return `${instanceId}${idPseudoSeparator}${element}`;
}
export function createChildId(arrayOrObjectId, indexOrProperty, { idSeparator = DEFAULT_ID_SEPARATOR } = {}) {
    return `${arrayOrObjectId}${idSeparator}${indexOrProperty}`;
}
