export function isObject(value) {
    return typeof value === "object" && value !== null;
}
export function isRecord(value) {
    return isObject(value) && !Array.isArray(value);
}
export function getValueByPath(from, path, defaultValue) {
    let result = from;
    for (const k of path) {
        if (Array.isArray(result) &&
            typeof k === "number" &&
            k >= 0 &&
            k < result.length) {
            result = result[k];
            continue;
        }
        else if (isRecord(result) && k in result) {
            result = result[k];
            continue;
        }
        return defaultValue;
    }
    return result;
}
